import React from "react";
import HomePage from "../src/pages/homepage/homepage.component";
import { Switch, Route , Link} from "react-router-dom";
import "./App.css";

const Topiclist = (props) => {
  console.log(props)
  return(
  <div>
  <Link to="/Topiclist/:id">details page</Link>
    <button onClick={()=> props.history.push("/Topicdetail")}>details page</button> 
    <h1>test page</h1>
    <Link to={`${props.match.url}/13`}>To topic 13!</Link>
    <Link to={`${props.match.url}/14`}>To topic 14!</Link>
    <Link to={`${props.match.url}/15`}>To topic 15!</Link>
  </div>
)
};

const Topicdetail =(props)=>{

  console.log(props)
  return (
    <div>
      <h1>topic detail page : {props.match.id}</h1>
    </div>
  )
}



function App() {
  return (
    <React.Fragment>
      <Switch>
        <Route exact path="/" component={HomePage} />
        <Route  exact path="/Topiclist" component={Topiclist} />
        <Route  path="/Topiclist/:id" component={Topicdetail}/>
      </Switch>
    </React.Fragment>
  );
}

export default App;
